package premitiveVariables;

public class Patterns {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=1;
		for(int i=1;i<=5;i++) {
			int k =1;
		for(int j=1;j<=n;j++) {
			
			System.out.print(k+" ");
			k++;
		}
		n++;
		System.out.println();
		}
		
	}

}
